import React, { useRef, useEffect, useState } from 'react';
import { motion, useInView, useScroll, useTransform } from 'framer-motion';

const cardStyle = {
  width: '100%',
  background: 'var(--card-bg, #fff)',
  borderRadius: 20,
  boxShadow: '0 6px 32px 0 rgba(60,60,60,0.08)',
  padding: '28px 32px',
  borderLeft: '6px solid #ff6347', // Modern accent color
  margin: 0,
  transition: 'box-shadow 0.2s, transform 0.2s',
  display: 'flex',
  flexDirection: 'column',
  gap: 6,
  position: 'relative',
};
const cardHoverStyle = {
  boxShadow: '0 12px 32px 0 rgba(60,60,60,0.16)',
  transform: 'translateY(-4px) scale(1.02)',
};

function Education({ aboutRef, aboutSectionRef, theme }) {
  const blockRef = useRef(null);
  const sectionRef = useRef(null);
  const inView = useInView(blockRef, { margin: '-100px 0px -100px 0px', once: true });
  const inViewSection = useInView(sectionRef, { margin: '0px 0px 0px 0px' });
  // Detect when About is 90% out of view
  const aboutInView90 = useInView(aboutSectionRef, { margin: '-90% 0px 0px 0px' });
  const [educationActive, setEducationActive] = useState(false);
  const [hovered, setHovered] = useState(-1);

  useEffect(() => {
    if (!aboutInView90) setEducationActive(true);
  }, [aboutInView90]);

  // Animation starts from the beginning when educationActive is true
  const { scrollYProgress } = useScroll({ target: sectionRef, offset: ['start 100%', 'start 0%'] });
  const overlayScale = useTransform(scrollYProgress, [0, 0.5], [4, 1]);
  const overlayVisible = useTransform(overlayScale, value => value > 1 ? 1 : 0);
  const showOverlay = useTransform(scrollYProgress, v => v > 0 ? 1 : 0);
  const contentOpacity = useTransform(scrollYProgress, [0.6, 0.8], [0, 1]);
  const contentY = useTransform(scrollYProgress, [0.6, 0.8], [40, 0]);

  const cards = [
    {
      title: '10th',
      school: 'Nagar Parishad Vidyalaya, Chandur Bazar',
      year: '2020',
      value: '92%',
      valueLabel: 'Percentage',
    },
    {
      title: '12th',
      school: 'Rular Institute of Science, Amravati',
      year: '2022',
      value: '80%',
      valueLabel: 'Percentage',
    },
    {
      title: 'B.Tech',
      school: 'MIT Academy of Engineering',
      year: '2022 - 2026',
      value: '8.29',
      valueLabel: 'CGPA',
    },
  ];

  return (
    <section
      id="education"
      className="education"
      ref={el => {
        aboutRef && (aboutRef.current = el);
        sectionRef.current = el;
      }}
      style={{
        position: "relative",
        minHeight: "100vh",
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        background: theme === 'light' ? '#fff' : '#111',
        transition: 'background 0.3s',
      }}
    >
      {/* Fade in overlay and content when About is 90% out of view */}
      {educationActive && inViewSection && (
        <motion.div
          className="education-hollow-overlay"
          initial={{ opacity: 0, y: 40 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, ease: 'easeOut' }}
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            width: "100vw",
            height: "100vh",
            display: 'flex',
            alignItems: "center",
            justifyContent: "center",
            pointerEvents: "none",
            zIndex: 20,
          }}
        >
          <motion.svg
            width="80vw"
            height="30vh"
            viewBox="0 0 1000 300"
            style={{ display: "block", overflow: "visible" }}
          >
            <motion.text
              x="50%"
              y="50%"
              textAnchor="middle"
              dominantBaseline="middle"
              fontFamily="Impact, Arial Black, sans-serif"
              fontWeight="900"
              fontSize="200"
              letterSpacing="0.1em"
              style={{
                filter: "drop-shadow(0 2px 8px rgba(0,0,0,0.2))",
                transition: "all 0.2s",
                userSelect: "none",
                fill: '#111',
                stroke: '#111',
                strokeWidth: 2,
              }}
            >
              <tspan x="50%" dy="0">EDUCATION</tspan>
            </motion.text>
          </motion.svg>
        </motion.div>
      )}

      {educationActive && (
        <motion.div
          ref={blockRef}
          initial={{ opacity: 0, y: 40 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, ease: 'easeOut', delay: 0.2 }}
          style={{ width: '100%', maxWidth: 600, display: 'flex', flexDirection: 'column', gap: 36, alignItems: 'center' }}
        >
          <h2
            style={{
              fontSize: 36,
              fontWeight: 800,
              marginBottom: 24,
              letterSpacing: 1,
              textAlign: 'center',
              color: '#888',
            }}
          >
            Education
          </h2>
          <div style={{ marginTop: 180 }} />
          {cards.map((card, idx) => (
            <div
              key={card.title}
              style={{
                ...cardStyle,
                ...(hovered === idx ? cardHoverStyle : {}),
                cursor: 'pointer',
              }}
              onMouseEnter={() => setHovered(idx)}
              onMouseLeave={() => setHovered(-1)}
            >
              <div style={{ fontSize: 22, fontWeight: 700, color: '#ff6347', marginBottom: 2 }}>{card.title}</div>
              <div style={{ fontSize: 18, fontWeight: 600, color: '#222', marginBottom: 2 }}>{card.school}</div>
              <div style={{ fontSize: 15, color: '#666', marginBottom: 8 }}>{card.year}</div>
              <div style={{ fontSize: 18, fontWeight: 600, color: '#222' }}>
                <span style={{ color: '#ff6347', fontWeight: 800 }}>{card.value}</span>
                <span style={{ fontSize: 15, color: '#888', marginLeft: 8 }}>{card.valueLabel}</span>
              </div>
            </div>
          ))}
        </motion.div>
      )}
    </section>
  );
}

export default Education;
