import React, { useRef } from 'react';
import { motion, useViewportScroll, useTransform } from 'framer-motion';

function Education() {
  const { scrollY } = useViewportScroll();
  const sectionRef = useRef(null);

  // Adjust these values to match your layout/scroll position
  const sectionStart = 1200; // Approximate scrollY where Education section starts
  const sectionEnd = 1800;   // Approximate scrollY where Education section ends

  // Move content up by 120px as you scroll through the section
  const y = useTransform(scrollY, [sectionStart, sectionEnd], [0, -120]);

  return (
    <section
      id="education"
      className="education"
      ref={sectionRef}
      style={{ position: "relative", minHeight: "100vh" }}
    >
      <motion.div
        className="education-list"
        style={{
          y,
          marginTop: 0,
          pointerEvents: "auto"
        }}
      >
        <div className="stepper">
          <div className="step">
            <div className="step-content">
              <h3>10th</h3>
              <p><strong>School:</strong> Nagar Parishad Vidyalaya, Chandur Bazar</p>
              <p><strong>Year:</strong> 2020</p>
              <p><strong>Percentage:</strong> 92%</p>
            </div>
          </div>
          <div className="step">
            <div className="step-content">
              <h3>12th</h3>
              <p><strong>School:</strong> Rular Institute of Science, Amravati</p>
              <p><strong>Year:</strong> 2022</p>
              <p><strong>Percentage:</strong> 80%</p>
            </div>
          </div>
          <div className="step">
            <div className="step-content">
              <h3>B.Tech (MIT Academy of Engineering)</h3>
              <p><strong>Year:</strong> 2022 - 2026</p>
              <p><strong>CGPA:</strong> 8.29</p>
            </div>
          </div>
        </div>
      </motion.div>
    </section>
  );
}

export default Education;
