import React, { useRef } from 'react';
import { motion, useInView } from 'framer-motion';

function Education({ aboutRef }) {
  const blockRef = useRef(null);
  const inView = useInView(blockRef, { margin: '-100px 0px -100px 0px', once: true });

  return (
    <section
      id="education"
      className="education"
      ref={aboutRef}
      style={{ position: "relative", minHeight: "100vh", display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center' }}
    >
      <motion.div
        ref={blockRef}
        initial={{ opacity: 0, y: 60 }}
        animate={inView ? { opacity: 1, y: 0 } : {}}
        transition={{ duration: 0.9, ease: 'easeOut' }}
        style={{ width: '100%', maxWidth: 600, display: 'flex', flexDirection: 'column', gap: 32, alignItems: 'center' }}
      >
        <h2 style={{ fontSize: 36, fontWeight: 800, marginBottom: 24, letterSpacing: 1, textAlign: 'center' }}>Education</h2>
        <div style={{ background: 'var(--card-bg, #fff)', borderRadius: 16, boxShadow: '0 4px 24px rgba(0,0,0,0.08)', padding: 24, width: '100%' }}>
          <h3>10th</h3>
          <p><strong>School:</strong> Nagar Parishad Vidyalaya, Chandur Bazar</p>
          <p><strong>Year:</strong> 2020</p>
          <p><strong>Percentage:</strong> 92%</p>
        </div>
        <div style={{ background: 'var(--card-bg, #fff)', borderRadius: 16, boxShadow: '0 4px 24px rgba(0,0,0,0.08)', padding: 24, width: '100%' }}>
          <h3>12th</h3>
          <p><strong>School:</strong> Rular Institute of Science, Amravati</p>
          <p><strong>Year:</strong> 2022</p>
          <p><strong>Percentage:</strong> 80%</p>
        </div>
        <div style={{ background: 'var(--card-bg, #fff)', borderRadius: 16, boxShadow: '0 4px 24px rgba(0,0,0,0.08)', padding: 24, width: '100%' }}>
          <h3>B.Tech (MIT Academy of Engineering)</h3>
          <p><strong>Year:</strong> 2022 - 2026</p>
          <p><strong>CGPA:</strong> 8.29</p>
        </div>
      </motion.div>
    </section>
  );
}

export default Education;
