import React, { useRef } from 'react';
import { motion, useViewportScroll, useTransform } from 'framer-motion';
import sketingImg from '../sketing.jpg';
import paintingImg from '../sketching.jpg';
import travelingImg from '../traveling.jpg';

function Hobbies() {
  const ref = useRef(null);
  const { scrollY } = useViewportScroll();
  // Animate upward as you scroll: adjust the range as needed
  const y = useTransform(scrollY, [0, 600], [0, -150]);

  return (
    <section className="hobbies-collage" ref={ref}>
      <div className="hobbies-grid">
        <div className="hobbies-row">
          <motion.img
            src={sketingImg}
            alt=""
            className="glow-border"
            style={{
              marginTop: '100px',
              marginRight: '40px',
              width: '700px',
              height: '700px',
              objectFit: 'cover',
              y
            }}
          />
          <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
            <motion.p
              style={{
                fontSize: '3rem',
                fontWeight: 500,
                marginBottom: '1rem',
                maxWidth: '400px',
                textAlign: 'center',
                y
              }}
            >
              <span style={{ color: '#FFF9C4' }}>
                I vibe with skateboards, live for sketching wild ideas, and dream of stamping my passport on every pageâ€”
              </span>
              <br />
              <span style={{ color: '#F8BBD0' }}>
                because life's too short to stay still. Let's roll, draw, and explore!
              </span>
            </motion.p>
            <motion.img
              src={paintingImg}
              alt=""
              className="glow-border"
              style={{
                marginTop: '50px',
                width: '500px',
                height: '550px',
                objectFit: 'cover',
                y
              }}
            />
          </div>
        </div>
        <div className="hobbies-row">
          <motion.img
            src={travelingImg}
            alt=""
            className="glow-border"
            style={{
              width: '700px',
              height: '430px',
              objectFit: 'cover',
              position: 'relative',
              marginTop: '-440px',
              y
            }}
          />
        </div>
      </div>
    </section>
  );
}

export default Hobbies;
