import React, { useRef } from 'react';
import { motion, useInView } from 'framer-motion';

function Education({ aboutRef }) {
  // Refs for each step
  const step1Ref = useRef(null);
  const step2Ref = useRef(null);
  const step3Ref = useRef(null);
  // InView hooks for each step
  const step1InView = useInView(step1Ref, { margin: '-100px 0px -100px 0px', once: true });
  const step2InView = useInView(step2Ref, { margin: '-100px 0px -100px 0px', once: true });
  const step3InView = useInView(step3Ref, { margin: '-100px 0px -100px 0px', once: true });

  return (
    <section
      id="education"
      className="education"
      ref={aboutRef}
      style={{ position: "relative", minHeight: "100vh", display: 'flex', alignItems: 'center', justifyContent: 'center' }}
    >
      <div style={{ display: 'flex', width: '80%', maxWidth: 1200, minHeight: 500 }}>
        {/* Left Column: Text Info, each step animates up on scroll */}
        <div style={{ flex: 2, marginRight: 32, display: 'flex', flexDirection: 'column', justifyContent: 'center', gap: 32 }}>
          <motion.div
            ref={step1Ref}
            initial={{ opacity: 0, y: 60 }}
            animate={step1InView ? { opacity: 1, y: 0 } : {}}
            transition={{ duration: 0.7, ease: 'easeOut' }}
            style={{ background: 'var(--card-bg, #fff)', borderRadius: 16, boxShadow: '0 4px 24px rgba(0,0,0,0.08)', padding: 24 }}
          >
            <h3>10th</h3>
            <p><strong>School:</strong> Nagar Parishad Vidyalaya, Chandur Bazar</p>
            <p><strong>Year:</strong> 2020</p>
            <p><strong>Percentage:</strong> 92%</p>
          </motion.div>
          <motion.div
            ref={step2Ref}
            initial={{ opacity: 0, y: 60 }}
            animate={step2InView ? { opacity: 1, y: 0 } : {}}
            transition={{ duration: 0.7, ease: 'easeOut', delay: 0.1 }}
            style={{ background: 'var(--card-bg, #fff)', borderRadius: 16, boxShadow: '0 4px 24px rgba(0,0,0,0.08)', padding: 24 }}
          >
            <h3>12th</h3>
            <p><strong>School:</strong> Rular Institute of Science, Amravati</p>
            <p><strong>Year:</strong> 2022</p>
            <p><strong>Percentage:</strong> 80%</p>
          </motion.div>
          <motion.div
            ref={step3Ref}
            initial={{ opacity: 0, y: 60 }}
            animate={step3InView ? { opacity: 1, y: 0 } : {}}
            transition={{ duration: 0.7, ease: 'easeOut', delay: 0.2 }}
            style={{ background: 'var(--card-bg, #fff)', borderRadius: 16, boxShadow: '0 4px 24px rgba(0,0,0,0.08)', padding: 24 }}
          >
            <h3>B.Tech (MIT Academy of Engineering)</h3>
            <p><strong>Year:</strong> 2022 - 2026</p>
            <p><strong>CGPA:</strong> 8.29</p>
          </motion.div>
        </div>
        {/* Right Column: Graduation Cap SVG, sticky */}
        <div style={{ flex: 1, display: 'flex', alignItems: 'center', justifyContent: 'center', position: 'sticky', top: 120, height: 300 }}>
          <svg width="180" height="180" viewBox="0 0 64 64" fill="none" xmlns="http://www.w3.org/2000/svg">
            <rect width="64" height="64" rx="16" fill="#f5f5f5"/>
            <path d="M32 12L56 24L32 36L8 24L32 12Z" fill="#222"/>
            <path d="M32 36V52" stroke="#222" strokeWidth="2" strokeLinecap="round"/>
            <ellipse cx="32" cy="52" rx="8" ry="2" fill="#222" fillOpacity="0.2"/>
            <path d="M24 28V36C24 39.3137 27.5817 42 32 42C36.4183 42 40 39.3137 40 36V28" stroke="#ff6347" strokeWidth="2"/>
          </svg>
        </div>
      </div>
    </section>
  );
}

export default Education;
