import React, { useRef } from 'react';
import { motion, useInView } from 'framer-motion';

function Education({ aboutRef }) {
  const textBlockRef = useRef(null);
  const inView = useInView(textBlockRef, { margin: '-100px 0px -100px 0px', once: true });

  return (
    <section
      id="education"
      className="education"
      ref={aboutRef}
      style={{ position: "relative", minHeight: "100vh", display: 'flex', alignItems: 'center', justifyContent: 'center' }}
    >
      <div style={{ display: 'flex', width: '80%', maxWidth: 1200, minHeight: 500 }}>
        {/* Left Column: All text combined, animates up as a block */}
        <motion.div
          ref={textBlockRef}
          initial={{ opacity: 0, y: 60 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.9, ease: 'easeOut' }}
          style={{ flex: 2, marginRight: 32, background: 'var(--card-bg, #fff)', borderRadius: 16, boxShadow: '0 4px 24px rgba(0,0,0,0.08)', padding: 40, fontSize: 22, fontWeight: 500 }}
        >
          <div>
            <div style={{ marginBottom: 24 }}>
              <span style={{ fontWeight: 700, fontSize: 28 }}>10th</span> — <b>Nagar Parishad Vidyalaya, Chandur Bazar</b> (2020) — <span style={{ color: '#ff6347' }}>92%</span>
            </div>
            <div style={{ marginBottom: 24 }}>
              <span style={{ fontWeight: 700, fontSize: 28 }}>12th</span> — <b>Rular Institute of Science, Amravati</b> (2022) — <span style={{ color: '#ff6347' }}>80%</span>
            </div>
            <div>
              <span style={{ fontWeight: 700, fontSize: 28 }}>B.Tech</span> — <b>MIT Academy of Engineering</b> (2022 - 2026) — <span style={{ color: '#ff6347' }}>CGPA: 8.29</span>
            </div>
          </div>
        </motion.div>
        {/* Right Column: Graduation Cap SVG, sticky */}
        <div style={{ flex: 1, display: 'flex', alignItems: 'center', justifyContent: 'center', position: 'sticky', top: 120, height: 300 }}>
          <svg width="180" height="180" viewBox="0 0 64 64" fill="none" xmlns="http://www.w3.org/2000/svg">
            <rect width="64" height="64" rx="16" fill="#f5f5f5"/>
            <path d="M32 12L56 24L32 36L8 24L32 12Z" fill="#222"/>
            <path d="M32 36V52" stroke="#222" strokeWidth="2" strokeLinecap="round"/>
            <ellipse cx="32" cy="52" rx="8" ry="2" fill="#222" fillOpacity="0.2"/>
            <path d="M24 28V36C24 39.3137 27.5817 42 32 42C36.4183 42 40 39.3137 40 36V28" stroke="#ff6347" strokeWidth="2"/>
          </svg>
        </div>
      </div>
    </section>
  );
}

export default Education;
