import React, { useRef } from 'react';
import { motion, useInView, useScroll, useTransform } from 'framer-motion';

const cardStyle = {
  width: '100%',
  background: 'var(--card-bg, #fff)',
  borderRadius: 20,
  boxShadow: '0 6px 32px 0 rgba(60,60,60,0.08)',
  padding: '28px 32px',
  borderLeft: '6px solid #ff6347', // Modern accent color
  margin: 0,
  transition: 'box-shadow 0.2s, transform 0.2s',
  display: 'flex',
  flexDirection: 'column',
  gap: 6,
  position: 'relative',
};
const cardHoverStyle = {
  boxShadow: '0 12px 32px 0 rgba(60,60,60,0.16)',
  transform: 'translateY(-4px) scale(1.02)',
};

function Education({ aboutRef }) {
  const blockRef = useRef(null);
  const sectionRef = useRef(null);
  const inView = useInView(blockRef, { margin: '-100px 0px -100px 0px', once: true });
  const inViewSection = useInView(sectionRef, { margin: '0px 0px 0px 0px' });
  const [hovered, setHovered] = React.useState(-1);

  // Overlay and content animation: start strictly when Education section is at the top of the viewport
  // Offset: 'start start' means animation starts when Education top is at the top of the viewport
  //         'end start' means animation ends when Education bottom is at the top of the viewport
  const { scrollYProgress } = useScroll({ target: sectionRef, offset: ['start start', 'end start'] });
  const overlayScale = useTransform(scrollYProgress, [0, 0.25], [4, 1]);
  const overlayVisible = useTransform(overlayScale, value => value > 1 ? 1 : 0);
  const showOverlay = useTransform(scrollYProgress, v => v > 0 ? 1 : 0);

  // Animate content appearance: fade in and slide up after overlay is nearly done
  const contentOpacity = useTransform(scrollYProgress, [0.18, 0.25], [0, 1]);
  const contentY = useTransform(scrollYProgress, [0.18, 0.25], [40, 0]);

  const cards = [
    {
      title: '10th',
      school: 'Nagar Parishad Vidyalaya, Chandur Bazar',
      year: '2020',
      value: '92%',
      valueLabel: 'Percentage',
    },
    {
      title: '12th',
      school: 'Rular Institute of Science, Amravati',
      year: '2022',
      value: '80%',
      valueLabel: 'Percentage',
    },
    {
      title: 'B.Tech',
      school: 'MIT Academy of Engineering',
      year: '2022 - 2026',
      value: '8.29',
      valueLabel: 'CGPA',
    },
  ];

  return (
    <section
      id="education"
      className="education"
      ref={el => {
        aboutRef && (aboutRef.current = el);
        sectionRef.current = el;
      }}
      style={{ position: "relative", minHeight: "100vh", display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center' }}
    >
      {/* Cinematic EDUCATION overlay, only visible while scaling down and section is in view */}
      {inViewSection && (
        <motion.div
          className="education-hollow-overlay"
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            width: "100vw",
            height: "100vh",
            display: showOverlay ? 'flex' : 'none',
            alignItems: "center",
            justifyContent: "center",
            pointerEvents: "none",
            zIndex: 20,
            scale: overlayScale,
            opacity: overlayVisible,
          }}
        >
          <motion.svg
            width="80vw"
            height="30vh"
            viewBox="0 0 1000 300"
            style={{ display: "block", overflow: "visible" }}
          >
            <motion.text
              x="50%"
              y="50%"
              textAnchor="middle"
              dominantBaseline="middle"
              fontFamily="Impact, Arial Black, sans-serif"
              fontWeight="900"
              fontSize="200"
              letterSpacing="0.1em"
              style={{
                filter: "drop-shadow(0 2px 8px rgba(0,0,0,0.2))",
                transition: "all 0.2s",
                userSelect: "none",
                fill: '#111',
                stroke: '#111',
                strokeWidth: 2,
              }}
            >
              <tspan x="50%" dy="0">EDUCATION</tspan>
            </motion.text>
          </motion.svg>
        </motion.div>
      )}

      {/* Education content always visible */}
      <motion.div
        ref={blockRef}
        initial={false}
        animate={{ opacity: contentOpacity, y: contentY }}
        transition={{ duration: 0.9, ease: 'easeOut' }}
        style={{ width: '100%', maxWidth: 600, display: 'flex', flexDirection: 'column', gap: 36, alignItems: 'center' }}
      >
        <h2
          style={{
            fontSize: 36,
            fontWeight: 800,
            marginBottom: 24,
            letterSpacing: 1,
            textAlign: 'center',
            color: '#888',
          }}
        >
          Education
        </h2>
        <div style={{ marginTop: 180 }} />
        {cards.map((card, idx) => (
          <div
            key={card.title}
            style={{
              ...cardStyle,
              ...(hovered === idx ? cardHoverStyle : {}),
              cursor: 'pointer',
            }}
            onMouseEnter={() => setHovered(idx)}
            onMouseLeave={() => setHovered(-1)}
          >
            <div style={{ fontSize: 22, fontWeight: 700, color: '#ff6347', marginBottom: 2 }}>{card.title}</div>
            <div style={{ fontSize: 18, fontWeight: 600, color: '#222', marginBottom: 2 }}>{card.school}</div>
            <div style={{ fontSize: 15, color: '#666', marginBottom: 8 }}>{card.year}</div>
            <div style={{ fontSize: 18, fontWeight: 600, color: '#222' }}>
              <span style={{ color: '#ff6347', fontWeight: 800 }}>{card.value}</span>
              <span style={{ fontSize: 15, color: '#888', marginLeft: 8 }}>{card.valueLabel}</span>
            </div>
          </div>
        ))}
      </motion.div>
    </section>
  );
}

export default Education;
