import React from 'react';
import { motion, useViewportScroll, useTransform } from 'framer-motion';
import aboutImage from './formal_photo.png'; // Use your own image if available

function About() {
  const { scrollY } = useViewportScroll();
  // Parallax effect for image
  const y = useTransform(scrollY, [0, 500], [0, -100]);

  return (
    <section className="split-section" style={{ minHeight: '100vh', display: 'flex', background: '#181828', color: '#fff' }}>
      <div className="split-left" style={{ flex: 1, display: 'flex', flexDirection: 'column', justifyContent: 'center', padding: '4rem' }}>
        <h1 style={{ fontSize: '3rem', fontWeight: 'bold' }}>ABOUT ME</h1>
        <h2 style={{ color: '#ffb6b6', fontSize: '2rem', margin: '1rem 0' }}>Cloud Engineer & Developer</h2>
        <p style={{ fontSize: '1.2rem', color: '#fff' }}>
          I'm Namita Dewang, a Computer Engineer with a passion for cloud computing and full-stack development. With expertise in cloud technologies and a strong foundation in Java, I bring ideas to life through code. My technical toolkit includes MySQL, C++, Linux, Red Hat, Node.js, and React, allowing me to build robust and scalable solutions.
        </p>
        <p style={{ fontSize: '1.2rem', color: '#fff' }}>
          What drives me is the opportunity to solve complex problems and create efficient, user-centric applications. I'm constantly exploring new technologies and best practices to enhance my skills and deliver innovative solutions that make a difference.
        </p>
      </div>
      <motion.div
        className="split-right"
        style={{
          flex: 1,
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          position: 'relative',
          y,
        }}
      >
        <img src={aboutImage} alt="About" style={{ width: '100%', borderRadius: '1rem', boxShadow: '0 8px 32px rgba(0,0,0,0.3)' }} />
      </motion.div>
    </section>
  );
}

export default About;
