import React from 'react';
import { motion } from 'framer-motion';

function Home({ hideHome, homeScale, homeOpacity, isTransitioning, homeRef, theme, profilePhoto }) {
  if (hideHome) return null;
  return (
    <motion.section
      id="home"
      className="home"
      ref={homeRef}
      style={{
        scale: homeScale,
        opacity: homeOpacity,
        zIndex: 30,
        position: isTransitioning ? "fixed" : "absolute",
        top: 0,
        left: 0,
        width: "100vw",
        height: "100vh",
        background: theme === 'light' ? '#fff' : '#111',
        overflow: "hidden",
        pointerEvents: isTransitioning ? "auto" : "none",
      }}
      initial={false}
      animate={false}
    >
      <div className="home-content">
        <motion.div 
          className="text-content"
          initial={{ x: -100, opacity: 0 }}
          animate={{ x: 0, opacity: 1 }}
          transition={{ duration: 0.8 }}
        >
          <h1>Hello, I'm <span>Namita Dewang</span></h1>
          <p className="tagline">Cloud engineer | React Enthusiast | Problem Solver | JAVA Developer</p>
        </motion.div>
        <motion.div 
          className="profile-container"
          initial={{ x: 100, opacity: 0 }}
          animate={{ x: 0, opacity: 1 }}
          transition={{ duration: 0.8 }}
        >
          <motion.div
            className="background-animation"
            animate={{
              rotate: 360,
              scale: [1, 1.1, 1],
            }}
            transition={{
              duration: 15,
              repeat: Infinity,
              ease: "linear"
            }}
          />
          <motion.div 
            className="profile-image"
            whileHover={{ scale: 1.05 }}
            transition={{ type: "spring", stiffness: 300 }}
          >
            <img src={profilePhoto} alt="Namita Dewang" />
          </motion.div>
        </motion.div>
      </div>
    </motion.section>
  );
}

export default Home;
