import React from 'react';
import { motion, useViewportScroll, useTransform } from 'framer-motion';
import sketingImg from '../sketing.jpg';
import paintingImg from '../sketching.jpg';
import travelingImg from '../traveling.jpg';

function Hobbies() {
  const { scrollY } = useViewportScroll();
  const textY = useTransform(scrollY, [0, 400], [0, -100]); // Adjust as needed

  return (
    <section className="hobbies-collage">
      <div className="hobbies-grid">
        <div className="hobbies-row">
          <img src={sketingImg} alt="" style={{ marginTop: '100px', marginRight: '40px', width: '700px', height: '700px', objectFit: 'cover' }} />
          <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
            <motion.p
              style={{
                fontSize: '3rem',
                fontWeight: 500,
                marginBottom: '1rem',
                maxWidth: '400px',
                textAlign: 'center',
                y: textY
              }}
            >
              <span style={{ color: '#FFF9C4' }}>
                I vibe with skateboards, live for sketching wild ideas, and dream of stamping my passport on every pageâ€”
              </span>
              <br />
              <span style={{ color: '#F8BBD0' }}>
                because life's too short to stay still. Let's roll, draw, and explore!
              </span>
            </motion.p>
            <img src={paintingImg} alt="" style={{ marginTop: '50px', width: '500px', height: '550px', objectFit: 'cover' }} />
          </div>
        </div>
        <div className="hobbies-row">
          <img src={travelingImg} alt="" style={{ width: '700px', height: '430px', objectFit: 'cover', position: 'relative', marginTop: '-440px' }} />
        </div>
      </div>
    </section>
  );
}

export default Hobbies;
