import React, { useRef } from 'react';
import { motion, useInView, useScroll, useTransform } from 'framer-motion';

const cardStyle = {
  width: '100%',
  background: 'var(--card-bg, #fff)',
  borderRadius: 20,
  boxShadow: '0 6px 32px 0 rgba(60,60,60,0.08)',
  padding: '28px 32px',
  borderLeft: '6px solid #ff6347', // Modern accent color
  margin: 0,
  transition: 'box-shadow 0.2s, transform 0.2s',
  display: 'flex',
  flexDirection: 'column',
  gap: 6,
  position: 'relative',
};
const cardHoverStyle = {
  boxShadow: '0 12px 32px 0 rgba(60,60,60,0.16)',
  transform: 'translateY(-4px) scale(1.02)',
};

function Education({ aboutRef }) {
  const blockRef = useRef(null);
  const eduHeadlineRef = useRef(null);
  const [hovered, setHovered] = React.useState(-1);

  // Scroll-based animation for EDUCATION headline
  const { scrollY } = useScroll({ target: eduHeadlineRef, offset: ["start start", "end start"] });
  // Scale from 2.5 to 1, opacity from 1 to 0, y from 0 to -60 as you scroll
  const scale = useTransform(scrollY, [0, 200], [2.5, 1]);
  const opacity = useTransform(scrollY, [0, 200, 300], [1, 0.7, 0]);
  const y = useTransform(scrollY, [0, 200], [0, -60]);

  const cards = [
    {
      title: '10th',
      school: 'Nagar Parishad Vidyalaya, Chandur Bazar',
      year: '2020',
      value: '92%',
      valueLabel: 'Percentage',
    },
    {
      title: '12th',
      school: 'Rular Institute of Science, Amravati',
      year: '2022',
      value: '80%',
      valueLabel: 'Percentage',
    },
    {
      title: 'B.Tech',
      school: 'MIT Academy of Engineering',
      year: '2022 - 2026',
      value: '8.29',
      valueLabel: 'CGPA',
    },
  ];

  return (
    <section
      id="education"
      className="education"
      ref={aboutRef}
      style={{ position: "relative", minHeight: "100vh", display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center' }}
    >
      {/* Scroll-animated EDUCATION headline */}
      <motion.h1
        ref={eduHeadlineRef}
        style={{
          fontSize: 64,
          fontWeight: 900,
          letterSpacing: 2,
          color: '#222',
          textAlign: 'center',
          margin: '40px 0 32px 0',
          lineHeight: 1.1,
          textTransform: 'uppercase',
          filter: 'drop-shadow(0 2px 8px rgba(0,0,0,0.08))',
          position: 'relative',
          zIndex: 2,
          scale,
          opacity,
          y,
        }}
      >
        EDUCATION
      </motion.h1>
      <motion.div
        ref={blockRef}
        initial={{ opacity: 0, y: 60 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.9, ease: 'easeOut' }}
        style={{ width: '100%', maxWidth: 600, display: 'flex', flexDirection: 'column', gap: 36, alignItems: 'center' }}
      >
        {cards.map((card, idx) => (
          <div
            key={card.title}
            style={{
              ...cardStyle,
              ...(hovered === idx ? cardHoverStyle : {}),
              cursor: 'pointer',
            }}
            onMouseEnter={() => setHovered(idx)}
            onMouseLeave={() => setHovered(-1)}
          >
            <div style={{ fontSize: 22, fontWeight: 700, color: '#ff6347', marginBottom: 2 }}>{card.title}</div>
            <div style={{ fontSize: 18, fontWeight: 600, color: '#222', marginBottom: 2 }}>{card.school}</div>
            <div style={{ fontSize: 15, color: '#666', marginBottom: 8 }}>{card.year}</div>
            <div style={{ fontSize: 18, fontWeight: 600, color: '#222' }}>
              <span style={{ color: '#ff6347', fontWeight: 800 }}>{card.value}</span>
              <span style={{ fontSize: 15, color: '#888', marginLeft: 8 }}>{card.valueLabel}</span>
            </div>
          </div>
        ))}
      </motion.div>
    </section>
  );
}

export default Education;
